[asy]import graph;

draw(Circle((0,0),25),black);

draw((0,0)--(7,18),Arrow);

draw((0,0)--(0,25));

draw((0,0)--(15,-20));

label("WIN",(10,10),S);

label("LOSE",(-8,-8),N);

dot((0,0));

[/asy]
