pair A=(0,5), B=(8,0), C=(0,0), P=(1.5,1.7);
draw(A--B--C--cycle);
draw((A+C)/2--(A+B)/2,dashed);
dot(P);
label("$A$",A,NW);
label("$B$",B,E);
label("$C$",C,SW);
label("$P$",P,E);
label("$D$",(A+C)/2,W);
label("$E$",(A+B)/2,NE);
draw((0,0.4)--(0.4,0.4)--(0.4,0));[/asy]

