draw((11,0)--(11,12),dashed);
draw((11.7,0)--(11.7,.7)--(11,.7));
label("$A$",(0,12),NW);
label("$B$",(11,12),NE);
label("$C$",(16,0),SE);
label("$E$",(11,0),S);
label("$D$",(0,0),SW);
label("12 cm",(-1,6),W);
label("16 cm",(8,-1),S);
[/asy]

