label("2",(5,5),W);
label("3",(6.5,4),S);
label("1",(8,4.5),E);
label("4",(10,5),N);

draw((5,0)--(5,4),dashed);
draw((8,0)--(8,4),dashed);
label("4",(5,2),W);
label("4",(8,2),E);

label("30",(2.5,3));
draw(Circle((2.5,3),0.8));

label("12",(6.5,1.5));
draw(Circle((6.5,1.5),0.8));

label("20",(10,2.5));
draw(Circle((10,2.5),0.8));

[/asy]

