path e=(5,5)--(5,-5)--(-5,-5)--(-5,5)--cycle;
fill(e,gray(0.6));
fill(a,gray(0.8));
fill(b,gray(0.8));
fill(c,gray(0.8));
fill(d,gray(0.8));
