unitsize(1 cm);

pair A, B, O, M;

O = (0,0);
A = dir(110);
B = dir(70);
M = (A + B)/2;

draw(Circle(O,1));
draw(A--B);
draw(O--M);

label("$A$", A, NW);
label("$B$", B, NE);
label("$O$", O, SW);
label("$M$", M, S);
