label("$C$",C,S);
label("2",(-0.45,0.95),E);
label("6",(3,0),N);
label("$F$",(5.55,1.95),E);
draw(O--F,linewidth(0.7));
label("2",(5.3,2.9),W);
label("2",(5.7,1),W);

