pen sm=fontsize(10);
draw((-1.5,0)--(15,0),Arrows);
draw((0,-4)--(0,5),Arrows);
draw(Circle((5,0),1));
draw(Circle((11,0),2));
pair A=(5,0), B=(5.4359,.9),C=(7,0), D=(11,0), E=(9.9995,-1.7337);
dot(A);
dot(C);
dot(D);
dot(B);
dot(E);
label(scale(0.7)*Label("(5,0)",(3.3,0),S));
label("$Q$",(7,0),N);
label(scale(0.7)*Label("(11,0)",(11,0),N));
label("$x$",(15,0),S);
label("$y$",(0,5),W);
draw((1,3.46410162)--(14,-4.04145188),Arrows);
draw(A--B);
draw(D--E);
label("$A$", A, SW, sm);
label("$B$", B, N, sm);
label("$D$", D, SE, sm);
label("$C$", E, S, sm);
label("$1$", (A+B)/2, W, sm);
label("$2$", (D+E)/2, SE, sm);
draw(rightanglemark(A,B,C));
draw(rightanglemark(C,E,D));
label("$b$", (A+C)/2, SE, sm);
label("$c$", (C+D)/2, NW, sm);
[/asy]

