triple B = (1,0,0);
triple C = (0,1,0);
triple D = (0,0,1);
draw(A--B--C--A);
draw(A--D,dashed);
draw(C--D--B,dashed);
label("$A$",A,NW);
label("$B$",B,W);
label("$C$",C,S);
label("$D$",D,NW);
triple M = (0,0.5,0.5);
draw(A--M--B,dashed);
label("$M$",M,NE);
triple G = B/3 + 2*M/3;
draw(A--G,dashed);
label("$G$",G,S);

[/asy]

