C=(9,5);
D=(0,5);
EE = (C + D)/2;
F=(3,0);
G=(6,0);
I=(4.5,5);
H = extension(A, C, EE, F);
J = extension(A, C, EE, G);
draw(A--B--C--D--cycle);
