unitsize(1 cm);

pair A, B, C, D, EE, F;

A = (0,0);
B = (1,0);
C = (1 + sqrt(2),sqrt(2));
D = (sqrt(2),sqrt(2));
EE = (sqrt(2),0);
F = (0,sqrt(2));

draw(A--B--C--D--EE--F--cycle);
draw(rightanglemark(B,A,F,2));
draw(rightanglemark(D,EE,C,2));

label("$A$", A, SW);
label("$B$", B, SE);
label("$C$", C, NE);
label("$D$", D, NW);
label("$E$", EE, S);
label("$F$", F, W);
[/asy]

