draw(A--B--D);
draw(A--V--B);
draw(C--F);

label("$\ell$", (0.8,-1/4), E);

dot("$A$", A, SW);
dot("$B$", B, SE);
dot("$C$", C, S);
dot("$D$", D, S);
dot("$F$", F, N);
dot("$V$", V, SW);
