\[c = \frac{a + 2b}{3} = \frac{(1 + i) + 2(4 + 7i)}{3} = \boxed{3 + 5i}.\][asy]
unitsize(1 cm);

pair A, B, C;

A = (1,1);
B = (4,7);
C = interp(A,B,2/3);

draw(A--B);

dot("$a$", A, NW);
dot("$b$", B, NW);
dot("$c$", C, NW);
[/asy]

