draw(O--P,Arrow3(6));
draw(O--Q,Arrow3(6));
draw(Q--R,Arrow3(6));
draw((P + 0.5*(R - Q))--(P + 0.5*(R - Q) + 0.2*(1,2,3)),Arrow3(6));

label("$\begin{pmatrix} 1 \\ 2 \\ 3 \end{pmatrix}$", P, N);
label("$\begin{pmatrix} -1 \\ 0 \\ 5 \end{pmatrix}$", Q, W);
label("$\begin{pmatrix} -1 \\ -1 \\ 2 \end{pmatrix}$", (Q + R)/2, S);
[/asy]

