unitsize (2 cm);

pair A, B, C, D, E, F, G, O;

A = dir(45);
B = dir(90);
C = dir(135);
D = dir(180);
E = dir(225);
F = dir(270);
G = dir(315);
O = (0,0);

filldraw(A--B--C--D--E--F--G--cycle,gray(0.7));
