unitsize(8 cm);

pair A, B, C, D, E, F, H, I;
real angleA = aCos(1/9);

B = (0,0);
C = (1,0);
A = extension(B, B + dir(90 - angleA/2), C, C + dir(90 + angleA/2));
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(A,C)*(B))/2;
F = (C + reflect(A,B)*(C))/2;
H = extension(B,E,C,F);
I = incenter(A,B,C);

draw(A--D,red);
draw(B--E,red);
draw(C--F,red);
draw(A--B--C--cycle);
draw(incircle(A,B,C));
draw(B--I);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, S);
label("$E$", E, NE);
label("$F$", F, NW);
label("$H$", H, SE, UnFill);
dot("$I$", I, dir(0));

label("$1$", (H + I)/2, E);
label("$1$", (D + I)/2, E);
label("$\frac{a}{2}$", (B + D)/2, S);
[/asy]

