F = (G + H)/2;
D = foot(A,B,C);

draw(A--B--C--cycle);
draw(A--foot(A,B,C));
draw(B--foot(B,C,A));
draw(C--foot(C,A,B));
draw(G--H);
draw(A--F);
draw(B--F);
draw(C--F);
draw(circle(O,abs(O - A)));
draw(A--D);

label("$A$", A, N);
label("$B$", B, SE);
label("$C$", C, SW);
label("$G$", G, NW);
label("$H$", H, NE);
label("$F$", F, S);
label("$O$", O, S);
label("$D$", D, S);
[/asy]

Then $BH = \frac{BC \cdot CD}{AC} = \frac{ac}{b}$ and $CH = \frac{BC \cdot BD}{AB} = \frac{ab}{c},$ so
\[\cos \angle AGF = \frac{\frac{a^2 c^2}{b^2} + \frac{a^2 b^2}{c^2} - c^2}{2 \cdot \frac{a^2 c}{bc}} = \frac{a^2 c^2 + a^2 b^2 - b^2 c^2}{2a^2 c}.\]Hence,
\begin{align*}
AF^2 &= AG^2 + GF^2 - 2 \cdot AG \cdot GF \cdot \cos \angle AGF \\
&= \frac{2}{3} (a^2 + b^2 + c^2) + \frac{9}{64} (4R^2 - a^2 - b^2 - c^2) \\
&\quad - 2 \cdot \sqrt{\frac{2}{3} (a^2 + b^2 + c^2)} \cdot \sqrt{\frac{9}{64} (4R^2 - a^2 - b^2 - c^2)} \cdot \frac{a^2 c^2 + a^2 b^2 - b^2 c^2}{2a^2 c} \\
&= \frac{2}{3} (a^2 + b^2 + c^2) + \frac{9}{64} (4R^2 - a^2 - b^2 - c^2) \\
&\quad - \frac{3}{8} \sqrt{(a^2 + b^2 + c^2)(4R^2 - a^2 - b^2 - c^2)} \cdot \frac{a^2 c^2 + a^2 b^2 - b^2 c^2}{2a^2 c}.
\end{align*}By symmetry,
\begin{align*}
BF^2 &= \frac{2}{3} (a^2 + b^2 + c^2) + \frac{9}{64} (4R^2 - a^2 - b^2 - c^2) \\
&\quad - \frac{3}{8} \sqrt{(a^2 + b^2 + c^2)(4R^2 - a^2 - b^2 - c^2)} \cdot \frac{b^2 a^2 + b^2 c^2 - a^2 c^2}{2b^2 a}
\end{align*}and
\begin{align*}
CF^2 &= \frac{2}{3} (a^2 + b^2 + c^2) + \frac{9}{64} (4R^2 - a^2 - b^2 - c^2) \\
&\quad - \frac{3}{8} \sqrt{(a^2 + b^2 + c^2)(4R^2 - a^2 - b^2 - c^2)} \cdot \frac{c^2 b^2 + c^2 a^2 - a^2 b^2}{2c^2 b}.
\end{align*}Therefore,
\begin{align*}
AF^2 + BF^2 + CF^2 &= 2 (a^2 + b^2 + c^2) + \frac{27}{64} (4R^2 - a^2 - b^2 - c^2) \\
&\quad - \frac{3}{8} \sqrt{(a^2 + b^2 + c^2)(4R^2 - a^2 - b^2 - c^2)} \\
&\quad \times \left( \frac{a^2 c^2 + a^2 b^2 - b^2 c^2}{2a^2 c} + \frac{b^2 a^2 + b^2 c^2 - a^2 c^2}{2b^2 a} + \frac{c^2 b^2 + c^2 a^2 - a^2 b^2}{2c^2 b} \right) \\
&= 2 (a^2 + b^2 + c^2) + \frac{27}{64} (4R^2 - a^2 - b^2 - c^2) \\
&= \boxed{\frac{41}{64} (a^2 + b^2 + c^2) + \frac{27}{16} R^2}.
\end{align*}

