size(50);
draw((0,0)--(4,0)--(4,4)--(0,4)--cycle);
draw((3,1)--(7,1)--(7,5)--(3,5)--cycle);
draw((0,0)--(3,1));
draw((4,0)--(7,1));
draw((4,4)--(7,5));
draw((0,4)--(3,5));
[/asy]
