size(200);
defaultpen(linewidth(.8pt)+fontsize(10pt));
draw((0,0)--(9,0)--(9,3)--(0,3)--(0,0)--cycle);
draw((17,3)--(12,3)--(12,0)--(21,0),dashed);
draw((21,3)--(17,3)--(16,0)--(16+3.2,-2.4)--(21,0)--(21,3)--cycle);
draw((17,3)--(21,0));

label("A", (0,3), NW);
label("B", (0,0), SW);
label("C", (9,0), SE);
label("D", (9,3), NE);
