draw((0,0)--10dir(180),linewidth(2));
draw((0,0)--10dir(67.5),linewidth(2));
draw((0,0)--10dir(0),dashed);

draw(10dir(180)..10dir(90)..10dir(0),linewidth(2));

draw((5,0)+5dir(180)..(5,0)+5dir(-90)..(5,0)+5dir(0),linewidth(2));

dot((0,0));
dot((5,0));

label("A",10dir(180),W);
label("B",10dir(0),E);
label("C",(0,0),SW);
