label("$A$",(0,0),SW);
label("$B$",(0,10),NW);
label("$C$",(10,10),NE);
label("$D$",(10,0),SE);
label("$A$",(20,10),NW);
label("$B$",(30,10),NE);
label("$C$",(30,0),SE);
label("$D$",(20,0),SW);
