Q = extension(C, C + dir(20), D, D + dir(160));
C = extension(P, P + dir(270), Q, Q + dir(270));

draw(Circle(O,5));
draw(A--B--C--D--cycle);
draw(O--P);
draw(O--Q);

label("$A$", A, NW);
label("$B$", B, SW);
