label("$D$", D, NE);
label("$O$", O, NE);
label("$P$", P, NW);
label("$Q$", Q, SE);
label("$r$", (O + P)/2, NW);
label("$r$", (O + Q)/2, SE);
label("$19$", (A + P)/2, W);
label("$26$", (B + P)/2, W);
label("$37$", (C + Q)/2, E);
label("$23$", (D + Q)/2, E);
[/asy]

