label("$B$", B, dir(0));

label("$C$", C, SE);

label("$D$", D, SW);

label("$E$", E, W);

dot("$O$", O, dir(0));

label("$P$", P, S);

label("$Q$", Q, dir(0));

label("$R$", R, W);

label("$1$", (O + P)/2, dir(0));

[/asy]

