label("$B$", B, NE);
label("$C$", C, SE);
label("$D$", D, SE);
label("$E$", E, S);
label("$F$", F, S);
label("$x$", (A + E)/2, S);
label("$y$", (D + F)/2, S);
label("$180$", (A + B)/2, NW);
label("$180$", (C + D)/2, NE);
label("$O$", O, N);
