A = intersectionpoint(arc(O,sqrt(5),0,180),graph((x^2)/8,0,5));
B = intersectionpoint(arc(O,sqrt(5),0,180),graph((x^2)/8,0,5));

draw(Circle(O,sqrt(5)));
draw(graph((x^2)/8,-2,5));
draw(O--A);
draw(O--B);
draw(C--A);

label("$x$", (5,0), E);
label("$y$", (0,4), N);
label("$A$", A, NE);
