real func (real x) {
  return(x^2);
}

draw(graph(func,-4,4));
draw((-4,0)--(4,0));
draw((0,-1)--(0,16));
draw((-3,-13)--(3,1));
draw((-3,-13)--(-3,func(-3)));
draw((3,1)--(3,func(3)));

