  if (x >= 2 && x <= 3) {y = 2*(x - 2);}
  return(y);
}

real funcg (real x) {
  return(func(-x));
}

int i, n;

for (i = -8; i <= 8; ++i) {
  draw((i,-8)--(i,8),gray(0.7));
  draw((-8,i)--(8,i),gray(0.7));
