dot(A);

dot(B);

dot(C);

dot(D);

dot(EE);

dot(F);

label("$E$",EE,S);

label("$F$",F,N);

X = intersectionpoint(A--B,EE--F);

Y = intersectionpoint(C--D,EE--F);

label("$X$",X,NNW);

label("$Y$",Y,NNW);

label("$A$",A,W);

